mapper.insert:
  insert into event (id, app_code, event_type,biz_id, request, `status`,execute_size, target_ip, gmt_create,
  gmt_modify, receive_chanel, execute_time,version
  )
values (#{id,jdbcType=BIGINT}, #{appCode,jdbcType=VARCHAR}, #{eventType,jdbcType=VARCHAR},
  #{bizId,jdbcType=VARCHAR}, #{request,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER},
  #{executeSize,jdbcType=INTEGER}, #{targetIp,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP},
  #{gmtModify,jdbcType=TIMESTAMP}, #{receiveChanel,jdbcType=VARCHAR}, #{executeTime,jdbcType=BIGINT}, #{version,jdbcType=BIGINT}
  )

mapper.select:
SELECT

FROM
event


        AND app_code = #{appCode}


        AND event_type = #{eventType}


        AND biz_id = #{bizId}


        AND version = #{version}

SELECT

FROM
event


        AND `status` = #{status}


        AND   execute_time >=#{startTime}  


        AND   execute_time < #{endTime}  


order by id
limit #{offset},#{limit}

SELECT

FROM
event


        AND id = #{id}



        AND app_code = #{appCode}



        AND event_type = #{eventType}


        AND biz_id = #{bizId}




    order by gmt_create desc limit #{start},#{pageSize}

SELECT count(id)
FROM
event


        AND id = #{id}



        AND app_code = #{appCode}



        AND event_type = #{eventType}


        AND biz_id = #{bizId}

mapper.update:
UPDATE event
status=#{status}, execute_time=#{executeTime}, execute_size=#{executeSize},
WHERE id=#{id}
